using System;
using System.Collections;

namespace OpenDentBusiness{
	
	///<summary></summary>
	public class Equipment{
		///<summary>Primary key.</summary>
		public long EquipmentNum;
		///<summary>Short description, need not be very unique.</summary>
		public string Description;
		///<summary>Must be unique among all pieces of equipment.  Auto-generated 3 char alpha numeric gives 42,875 unique serial numbers.  Zero never part of autogenerated serial number.</summary>
		public string SerialNumber;
		///<summary>Limit 2 char.</summary>
		public string ModelYear;
		///<summary>Date when this corporation obtained the equipment.</summary>
		public DateTime DatePurchased;
		///<summary>Normally 01-01-0001 if equipment still in possession.  Once sold, a date will be present.</summary>
		public DateTime DateSold;
		///<summary></summary>
		public double PurchaseCost;
		///<summary></summary>
		public double MarketValue;
		///<summary></summary>
		public string Location;
		///<summary></summary>

	}



	


}









